package com.example.app_jdp_gestion_gastos.ui.adaptersimport android.util.Logimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.example.app_jdp_gestion_gastos.data.model.Groupimport com.example.app_jdp_gestion_gastos.databinding.ItemGroupBindingimport java.text.SimpleDateFormatimport java.util.*class GroupAdapter(private val onItemClick: (Group) -> Unit) : RecyclerView.Adapter<GroupAdapter.GroupViewHolder>() {    private var groups: List<Group> = emptyList()    fun submitList(newGroups: List<Group>) {        if (groups != newGroups) {            groups = newGroups            notifyDataSetChanged()        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): GroupViewHolder {        val binding = ItemGroupBinding.inflate(LayoutInflater.from(parent.context), parent, false)        return GroupViewHolder(binding, onItemClick)    }    override fun onBindViewHolder(holder: GroupViewHolder, position: Int) {        val group = groups[position]        holder.bind(group)    }    override fun getItemCount(): Int = groups.size    class GroupViewHolder(private val binding: ItemGroupBinding, private val onItemClick: (Group) -> Unit)        : RecyclerView.ViewHolder(binding.root) {        fun bind(group: Group) {            binding.textViewGroupName.text = group.name            binding.textViewGroupMembers.text = "Miembros: ${group.members.size}"            // Manejo seguro de la fecha de creaci√≥n            val formattedDate = group.createdAt?.toDate()?.let {                SimpleDateFormat("dd/MM/yyyy", Locale.getDefault()).format(it)            } ?: "Fecha desconocida"            binding.textViewGroupCreatedAt.text = "Creado el: $formattedDate"            binding.root.setOnClickListener {                Log.d("GroupAdapter", "Grupo seleccionado: ${group.name}")                onItemClick(group)            }        }    }}