package com.example.app_jdp_gestion_gastos.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.example.app_jdp_gestion_gastos.data.model.Expenseimport com.example.app_jdp_gestion_gastos.data.model.Incomeimport com.example.app_jdp_gestion_gastos.databinding.ItemTransactionBindingclass StatsAdapter : RecyclerView.Adapter<StatsAdapter.TransactionViewHolder>() {    private val transactions = mutableListOf<Any>() // Puede contener Income o Expense    // Inflar el layout para cada item    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): TransactionViewHolder {        val binding = ItemTransactionBinding.inflate(LayoutInflater.from(parent.context), parent, false)        return TransactionViewHolder(binding)    }    // Vincular datos con el ViewHolder    override fun onBindViewHolder(holder: TransactionViewHolder, position: Int) {        val transaction = transactions[position]        holder.bind(transaction)    }    override fun getItemCount(): Int = transactions.size    // ViewHolder que contiene los elementos visuales de cada item    class TransactionViewHolder(private val binding: ItemTransactionBinding) : RecyclerView.ViewHolder(binding.root) {        fun bind(transaction: Any) {            if (transaction is Income) {                binding.tvAmount.text = "Ingreso: ${"%.2f".format(transaction.amount)} €"            } else if (transaction is Expense) {                binding.tvAmount.text = "Gasto: ${"%.2f".format(transaction.amount)} €"            }        }    }}