package com.example.app_jdp_gestion_gastos.ui.dialogimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.example.app_jdp_gestion_gastos.databinding.ItemStatDetailsBindingBinding  // Asegúrate de que la clase de enlace esté correctamente importadaclass StatsItemsAdapter(private val items: List<String>) :    RecyclerView.Adapter<StatsItemsAdapter.ViewHolder>() {    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {        // Inflamos el layout con el binding correcto        val binding = ItemStatDetailsBindingBinding.inflate(LayoutInflater.from(parent.context), parent, false)        return ViewHolder(binding)    }    override fun onBindViewHolder(holder: ViewHolder, position: Int) {        val item = items[position]        holder.bind(item)    }    override fun getItemCount(): Int = items.size    inner class ViewHolder(private val binding: ItemStatDetailsBindingBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(item: String) {            // Asignamos el valor del item al TextView en el layout            binding.tvStatDetail.text = item        }    }}