package com.example.login.ui.dialogimport android.app.Dialogimport android.os.Bundleimport android.text.SpannableStringimport android.text.style.StyleSpanimport android.view.Viewimport androidx.core.content.ContextCompatimport androidx.fragment.app.DialogFragmentimport com.example.app_jdp_gestion_gastos.Rimport com.google.android.material.dialog.MaterialAlertDialogBuilderimport com.google.android.material.snackbar.Snackbarimport android.graphics.Typefaceclass AyudaDialogo : DialogFragment() {    override fun onCreateDialog(savedInstanceState: Bundle?): Dialog {        val texto = "App desarrollada por David Serrano,\nJavier Mira y Pablo Silva"        val mensaje = SpannableString(texto)        val davidStart = texto.indexOf("David Serrano")        val davidEnd = davidStart + "David Serrano".length        val javierStart = texto.indexOf("Javier Mira")        val javierEnd = javierStart + "Javier Mira".length        val pabloStart = texto.indexOf("Pablo Silva")        val pabloEnd = pabloStart + "Pablo Silva".length        if (davidStart != -1) {            mensaje.setSpan(StyleSpan(Typeface.BOLD), davidStart, davidEnd, 0)        }        if (javierStart != -1) {            mensaje.setSpan(StyleSpan(Typeface.BOLD), javierStart, javierEnd, 0)        }        if (pabloStart != -1) {            mensaje.setSpan(StyleSpan(Typeface.BOLD), pabloStart, pabloEnd, 0)        }        val dialog = MaterialAlertDialogBuilder(requireContext())            .setTitle("Ayuda")            .setMessage(mensaje)            .setIcon(R.drawable.settings)            .setPositiveButton("OK") { dialogInterface, _ ->                view?.let { v ->                    Snackbar.make(v, "BotÃ³n Pulsado", Snackbar.LENGTH_SHORT).show()                }                dialogInterface.dismiss()            }            .setNegativeButton("Cancelar") { dialogInterface, _ ->                dialogInterface.dismiss()            }            .setBackground(ContextCompat.getDrawable(requireContext(), R.drawable.dialog_background))            .create()        dialog.window?.attributes?.windowAnimations = R.style.DialogAnimation        return dialog    }}