package com.example.login.ui.dialogimport android.app.Dialogimport android.content.Contextimport android.os.Bundleimport android.widget.Toastimport androidx.core.content.ContextCompatimport androidx.fragment.app.DialogFragmentimport com.example.app_jdp_gestion_gastos.Rimport com.google.android.material.dialog.MaterialAlertDialogBuilderclass AyudaDialogo : DialogFragment() {    private lateinit var contexto: Context    override fun onAttach(context: Context) {        super.onAttach(context)        this.contexto = context    }    override fun onCreateDialog(savedInstanceState: Bundle?): Dialog {        val dialog = MaterialAlertDialogBuilder(requireContext())            .setTitle("Ayuda")            .setMessage("App desarrollada por David Serrano,\nJavier Mira y Pablo Silva")            .setIcon(R.drawable.ajustes) // Ícono de información            .setPositiveButton("OK") { _, _ ->                Toast.makeText(requireContext(), "Botón Pulsado", Toast.LENGTH_SHORT).show()            }            .setBackground(ContextCompat.getDrawable(requireContext(), R.drawable.dialog_background)) // Fondo redondeado            .create()        // Agregar animación        dialog.window?.attributes?.windowAnimations = R.style.DialogAnimation        return dialog    }}