package com.example.app_jdp_gestion_gastos.ui.dialogimport android.app.Dialogimport android.os.Bundleimport android.view.LayoutInflaterimport androidx.appcompat.app.AlertDialogimport androidx.fragment.app.DialogFragmentimport androidx.fragment.app.viewModelsimport com.example.app_jdp_gestion_gastos.databinding.DialogCreateGroupBindingimport com.example.app_jdp_gestion_gastos.ui.viewmodel.GroupViewModelclass CreateGroupDialog : DialogFragment() {    private var _binding: DialogCreateGroupBinding? = null    private val binding get() = _binding!!    private val viewModel: GroupViewModel by viewModels({ requireParentFragment() })    override fun onCreateDialog(savedInstanceState: Bundle?): Dialog {        _binding = DialogCreateGroupBinding.inflate(LayoutInflater.from(context))        val dialog = AlertDialog.Builder(requireContext())            .setTitle("Crear Grupo")            .setView(binding.root)            .create()        binding.buttonCreate.setOnClickListener {            val groupName = binding.editTextGroupName.text.toString().trim()            if (groupName.isNotEmpty()) {                viewModel.createGroup(groupName)                dismiss() // Cierra el diálogo después de crear el grupo            } else {                binding.editTextGroupName.error = "El nombre no puede estar vacío"            }        }        binding.buttonCancel.setOnClickListener {            dismiss() // Cierra el diálogo si se presiona cancelar        }        return dialog    }    override fun onDestroyView() {        super.onDestroyView()        _binding = null    }}