package com.example.app_jdp_gestion_gastos.ui.dialogimport android.app.Dialogimport android.content.Contextimport android.os.Bundleimport androidx.appcompat.app.AlertDialogimport androidx.core.content.ContextCompatimport androidx.fragment.app.DialogFragmentimport com.example.app_jdp_gestion_gastos.Rimport com.google.android.material.dialog.MaterialAlertDialogBuilderclass LogoutDialogo: DialogFragment() {    private lateinit var contexto: Context    private lateinit var listener: onDialogoLogOutListener    override fun onAttach(context: Context) {        super.onAttach(context)        this.contexto = context        this.listener = context as onDialogoLogOutListener    }    override fun onCreateDialog(savedInstanceState: Bundle?): Dialog {        val dialog = MaterialAlertDialogBuilder(requireContext())            .setTitle("Cerrar Sesión")            .setIcon(R.drawable.logout)            .setMessage("¿Estás seguro que deseas cerrar la sesión?")            .setPositiveButton("SI") { _, _ ->                listener.dialogoLogOutSelected() // Cierra sesión            }            .setNegativeButton("NO") { _, _ -> }            .setBackground(ContextCompat.getDrawable(requireContext(), R.drawable.dialog_background)) // Fondo personalizado            .create()        // Aplicar animación de entrada y salida        dialog.window?.attributes?.windowAnimations = R.style.DialogAnimation        return dialog    }    // TODO: Cerrar la sesión    interface onDialogoLogOutListener {        fun dialogoLogOutSelected()    }}