package com.example.app_jdp_gestion_gastos.ui.groupimport android.app.Dialogimport android.os.Bundleimport android.view.LayoutInflaterimport android.widget.ArrayAdapterimport android.widget.ListViewimport androidx.appcompat.app.AlertDialogimport androidx.fragment.app.DialogFragmentimport com.example.app_jdp_gestion_gastos.Rimport com.example.app_jdp_gestion_gastos.data.model.Userclass SelectUserDialog(    private val users: List<User>,    private val onUserSelected: (User) -> Unit) : DialogFragment() {    override fun onCreateDialog(savedInstanceState: Bundle?): Dialog {        val view = LayoutInflater.from(requireContext()).inflate(R.layout.dialog_select_user, null)        val listView = view.findViewById<ListView>(R.id.lvUsers)        val adapter = ArrayAdapter(requireContext(), android.R.layout.simple_list_item_1, users.map { it.name })        listView.adapter = adapter        listView.setOnItemClickListener { _, _, position, _ ->            val selectedUser = users[position]            onUserSelected(selectedUser)            dismiss()        }        return AlertDialog.Builder(requireContext())            .setTitle("Selecciona un usuario")            .setView(view)            .setNegativeButton("Cancelar", null)            .create()    }}