package com.example.app_jdp_gestion_gastos.ui.viewmodelimport androidx.lifecycle.ViewModelimport androidx.lifecycle.ViewModelProviderimport com.example.app_jdp_gestion_gastos.data.repository.TransactionsRepositoryclass TransactionsViewModelFactory(    private val repository: TransactionsRepository) : ViewModelProvider.Factory {    override fun <T : ViewModel> create(modelClass: Class<T>): T {        if (modelClass.isAssignableFrom(TransactionsViewModel::class.java)) {            return TransactionsViewModel(repository) as T        }        throw IllegalArgumentException("Unknown ViewModel class")    }}